sp_widget.client_script:
function($rootScope, $scope, $timeout, $window, $location, spUtil) {
	var c = this;

	$scope.showNotification = true;

	$scope.computeTopOffset = function() {
		var styleObject = {};
		if($scope.showNotification) {
			styleObject.top = angular.element('div.sp-announcement-list').height();
		} else {
			styleObject.top = '0px';
		}

		if (c.data.leftNavExpanded) {
			styleObject.width = '250px';
		} else {
			styleObject.width = '72px';
		}

		return styleObject;
	};

	$scope.allocateWidthMinusScrollbar = function() {
		var divWidth = '0';
		if (c.data.leftNavExpanded) {
			divWidth = '250';
		} else {
			divWidth = '72';
		}

		var scrollbarElement = document.getElementById('scrollbar-measure');

		var scrollbarWidth = 10;
		if (scrollbarElement) {
		  scrollbarWidth = scrollbarElement.offsetWidth - scrollbarElement.clientWidth;
		  scrollbarWidth = scrollbarWidth == 0 ? 15 : scrollbarWidth;
	    }
		var newWidth = divWidth - scrollbarWidth;

		return {'width': newWidth + 'px'};
	};

	//
  //  $rootScope.$on('notificationHidden', function() {
  //      $scope.showNotification = false;
  //  });
	//

	$scope.clickOnMenu = function (menu) {
		console.dir(menu);
		if (menu.type == 'page') {
			return;
		}
		var subItemSelected = false;
		c.data.menu.items = c.data.menu.items.map(function (item) {
			if (item.type == 'Category') {
				if (item.sys_id == menu.sys_id) {
					item.showSub = !item.showSub;

					if (item.showSub) {
						item.isSelectedSection = true;
						if (item.isSelectedItem) {
							item.isSelectedItem = false;	
						}
					} else if (!item.showSub) {
						if (item.isSelectedSection) {
							item.isSelectedSection = false;
							subItemSelected = false;
							item.items.forEach(function(subitem) {
								if (subitem.isSelectedItem) {
									item.isSelectedItem = true;
									subItemSelected = true;
								}
							});
							if (!subItemSelected) {
								item.isSelectedItem = false;
							}
						}
					}

				} else {
					subItemSelected = false;
					item.items.forEach(function(subitem) {
						if (subitem.isSelectedItem) {
							if (item.showSub) {
								item.isSelectedSection = true;
								item.isSelectedItem = true;
							}

							subItemSelected = true;
						}
						return subitem;
					});
					if (!subItemSelected) {
						item.isSelectedSection = false;
						item.isSelectedItem = false;
					}

					item.showSub = false;
				}
			}
			return item;
		});

	};

	c.hideMenu = function() {
		$rootScope.$broadcast('hideMenu', true);
	};

	c.toggleMenuContext = function() {
		//c.data.menu.showFavorites = !c.data.menu.showFavorites;
	};

	c.toggleCollapse = function() {
		c.data.leftNavExpanded = !c.data.leftNavExpanded;
		c.server.update().then(function(response) {
			c.toggleLeftNavOnElements(c.data.leftNavExpanded);
		});
	};

	c.toggleLeftNavOnElements = function(isExpanded) {
		var mainElement = document.getElementsByTagName('main');
		if (mainElement[0] != null) {
			if (isExpanded) {
				mainElement[0].className = mainElement[0].className.replace(' leftNavCollapsed','');
				$rootScope.$broadcast('leftNavChanged', isExpanded);
			} else {
				if (mainElement[0].className.indexOf('leftNavCollapsed') == -1) {
					mainElement[0].className = mainElement[0].className + ' leftNavCollapsed';

					$rootScope.$broadcast('leftNavChanged', isExpanded);
				}
			}
		}
	};

	c.initialize = function () {
		c.data.leftNavExpanded = true;

		var userAgent = navigator.userAgent;
		c.canCollapse = !(userAgent != null && userAgent.indexOf('iPad') > -1 && userAgent.indexOf('Safari') > -1);
		if ($window.innerWidth > 991 && c.canCollapse) {
			c.toggleLeftNavOnElements(c.data.leftNavExpanded);
		} else {
			c.data.leftNavExpanded = true;
		}

		var params = $location.search();
		var currentPageId = params == undefined ? '' : params.id;
		var currentUrl = $location.url();

		c.data.menu.items = c.data.menu.items.map(function (item) {
			var menuItemPageId = item.href == undefined ? '' : item.href.replace('?id=','');

			if (item.type == 'page' && menuItemPageId == 'hisp_home' && currentUrl == '/hisp') {
				item.isSelectedItem = true;
			} else if (item.type == 'page' && menuItemPageId == 'hisp_service_catalog' && currentPageId == 'hisp_sc_item') {
				item.isSelectedItem = true;
			} else if (item.type == 'page' && menuItemPageId == currentPageId && menuItemPageId != '') {
				item.isSelectedItem = true;
			} else if (item.type == 'Category') {
				item.items = item.items.map(function(subitem) {
					var subMenuItemPageId = subitem.href == undefined ? '' : subitem.href.replace('?id=','');
					if (subMenuItemPageId == currentPageId && subMenuItemPageId != '') {
						item.isSelectedSection = true;
						item.showSub = true;
						subitem.isSelectedItem = true;
					}
					return subitem;
				});
			}  
			return item;
		});

	};

	angular.element(document).ready(function() {
		//c.initialize();
	});

	/*
	$rootScope.$on('pageChanged', function(event, pageID) {
        c.initialize();
  });
	*/
}

